// src/services/ollamaService/index.js
export const generateAnalysis = async (prompt) => {
    const response = await fetch('http://119.34.138.146:11434/api/generate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        model: 'llama3.2',
        prompt: prompt,
        stream: false
      })
    });

    if (!response.ok) {
      throw new Error('Failed to connect to Ollama');
    } else {
      console.info('Analysis error:', error);
    }

    return response.json();
  };
