// src/App.js
import React, { useState } from 'react';
import { Box, Card, CardContent, Typography, Tabs, Tab } from '@mui/material';
import { TrendingUp, BookOpen, MessageSquare } from 'lucide-react';
import { StockInput } from './components/StockInput';
import { PriceChart } from './components/PriceChart';
import { AnalysisCard } from './components/AnalysisCard';
import { useStockAnalysis } from './hooks/useStockAnalysis';

const sampleData = [
  { date: '2024-01', price: 150 },
  { date: '2024-02', price: 155 },
  { date: '2024-03', price: 165 },
  { date: '2024-04', price: 158 },
  { date: '2024-05', price: 170 }
];

function App() {
  const [stockSymbol, setStockSymbol] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const { analysis, loading, error, performAnalysis } = useStockAnalysis();

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    const types = ['technical', 'fundamental', 'sentiment'];
    performAnalysis(types[newValue], stockSymbol);
  };

  const TabPanel = ({ children, value, index }) => (
    <div hidden={value !== index}>
      {value === index && <Box>{children}</Box>}
    </div>
  );

  return (
    <Box sx={{ maxWidth: 1200, mx: 'auto', p: 3 }}>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Typography variant="h5" sx={{ mb: 3 }}>
            Stock Analysis Dashboard
          </Typography>

          <StockInput 
            value={stockSymbol}
            onChange={setStockSymbol}
            error={error}
          />

          <PriceChart data={sampleData} />
        </CardContent>
      </Card>

      <Box sx={{ width: '100%' }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          centered
          sx={{ mb: 3 }}
        >
          <Tab 
            icon={<TrendingUp size={20} />} 
            label="Technical" 
            iconPosition="start"
          />
          <Tab 
            icon={<BookOpen size={20} />} 
            label="Fundamental" 
            iconPosition="start"
          />
          <Tab 
            icon={<MessageSquare size={20} />} 
            label="Sentiment" 
            iconPosition="start"
          />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          <AnalysisCard
            title="Technical Analysis"
            icon={TrendingUp}
            content={analysis.technical}
            loading={loading}
          />
        </TabPanel>
        <TabPanel value={tabValue} index={1}>
          <AnalysisCard
            title="Fundamental Analysis"
            icon={BookOpen}
            content={analysis.fundamental}
            loading={loading}
          />
        </TabPanel>
        <TabPanel value={tabValue} index={2}>
          <AnalysisCard
            title="Sentiment Analysis"
            icon={MessageSquare}
            content={analysis.sentiment}
            loading={loading}
          />
        </TabPanel>
      </Box>
    </Box>
  );
}

export default App;
